package Lesson4;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int donut = 3;
		while (donut > 0) {
			System.out.println("I am busy eating the donut");
			donut -= 1;
		}
		
		System.out.println("I have eaten all the donuts!");
		

	}

}
